import React from 'react'

export default function Todoli(props) {


    function handleUl(event){
        if(event.target.tagName === 'LI'){
            event.target.classList.toggle('checked');
            const isclasschecked = event.target.classList.contains('checked');
            console.log(isclasschecked);
            if(isclasschecked){
                event.target.style.textDecoration = 'line-through';
                event.target.style.opacity = '0.4';
            }
            else{
                event.target.style.textDecoration = 'none';
                event.target.style.opacity = '1';
            }
        }
    }

    function handleUldouble(event){
        if(event.target.tagName === 'LI'){
            event.target.remove();
        }
    }



    return (
        <div>
            <li onClick={props.handleUl} onDoubleClick={props.handleUldouble} className="li_class" id={props.todoList}>{props.todoList}</li>
        </div>
    )
}
